import * as React from 'react'
import * as a from '../files/assets/social@2x.png'
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import { withStyles, WithStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { communityAction } from '../redux/action/loan-action'
import { connect } from "react-redux";
//import * as a from '../files/assets/social@2x.png'
import './loan-field.css'

const styles = theme => ({
    card: {
        width: 330,
    },
    media: {
        height: 0,
        padding: '30.25%',
        margin: '6%'
        // 16:9
    },

})

class CardFolder extends React.Component {
    componentDidMount() {
        this.props.dispatch(communityAction())
    }
    render() {
        const {
            classes,
            communityMenu
        } = this.props
        window.scrollTo({ top: 0, left: 0, behavior: 'smooth' });

        console.log(communityMenu)
        const cardValue = communityMenu && communityMenu.loanValue && communityMenu.loanValue.loanData && communityMenu.loanValue.loanData.default
        console.log(cardValue, "checkksas")
        const cardData = cardValue && cardValue.user

        return (
            <div className="pa2 ma3">
                <div className="flex flex-row">
                    <div className="flex w-40 justify-end">
                        <div className="w-30 b">
                            <h1>Meet Our Community</h1>
                            <div className="flex w-80 justify-end pr3">
                                <Button variant="contained" component="span" className={classes.button}>
                                    KNOW MORE
        </Button>
                            </div>
                            <div className="flex w-100 justify-end pr3">
                                <p>
                                    Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore
                            </p>
                            </div>
                        </div>

                    </div>
                    <div className="w-60 flex flex-row pa4 cardStyle">
                        <div className="w-50 flex pa2">
                            <Card className={classes.card}>
                                <CardMedia
                                    className={classes.media}
                                    image={cardData && cardData[0] && cardData[0].image ? cardData[0].image : ''}
                                    title="Paella dish"
                                />

                            </Card>
                        </div>
                        <div className="w-50 pa2 flex">
                            <Card className={classes.card}>
                                <CardContent>

                                    <Typography variant="h5" component="h2" className="pv2">
                                        {cardData && cardData[0] && cardData[0].image ? cardData[0].siteName : ''}

                                    </Typography>

                                    <Typography variant="body2" component="p" className="pv2">
                                        {cardData && cardData[0] && cardData[0].content ? cardData[0].content : ''}
                                    </Typography>
                                </CardContent>
                            </Card>
                        </div>
                    </div>
                </div>
                <div className="w-60 flex flex-row pa4 cardStyle">
                    <div className="w-50 flex pa2">
                        <Card className={classes.card}>
                            <CardMedia
                                className={classes.media}
                                image={cardData && cardData[1] && cardData[1].image ? cardData[1].image : ''}
                                title="Paella dish"
                            />

                            {/* <CardContent>
        <Typography  color="textSecondary" gutterBottom>
          Word of the Day
        </Typography>
        <Typography variant="h5" component="h2">
          {cardData && cardData[0] && cardData[0].image ?cardData[0].siteName : ''}
          
        </Typography>
        <Typography  color="textSecondary">
          adjective
        </Typography>
        <Typography variant="body2" component="p">
          {cardData && cardData[0] && cardData[0].content ?cardData[0].content : ''}
          <br />
          {'"a benevolent smile"'}
        </Typography>
      </CardContent> */}
                        </Card>
                    </div>
                    <div className="w-50 pa2 flex">
                        <Card className={classes.card}>
                            <CardContent>

                                <Typography variant="h5" component="h2" className="pv2">
                                    {cardData && cardData[1] && cardData[1].image ? cardData[1].siteName : ''}

                                </Typography>

                                <Typography variant="body2" component="p" className="pv2">
                                    {cardData && cardData[1] && cardData[1].content ? cardData[1].content : ''}

                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                </div>
                
                <div className="w-100 flex reverse pa4 cardStyle justify-end">
                    <div className="w-50 flex">
                <div className="w-30 flex justify-end mh4">
                    <h3>Lorem ipsum dolor sit amet, consetetur sadipscing elitr, sed diam nonumy eirmod tempor invidunt ut labore et dolore magna aliquyam erat, sed diam voluptua</h3>
                </div>
                </div>
                    <div className="flex pa2">
                        <Card className={classes.card}>
                            <CardMedia
                                className={classes.media}
                                image={cardData && cardData[2] && cardData[2].image ? cardData[2].image : ''}
                                title="Paella dish"
                            />

                        </Card>
                    </div>
                    <div className="pa2 flex">
                        <Card className={classes.card}>
                            <CardContent>

                                <Typography variant="h5" component="h2" className="pv2">
                                    {cardData && cardData[2] && cardData[2].image ? cardData[2].siteName : ''}

                                </Typography>

                                <Typography variant="body2" component="p" className="pv2">
                                    {cardData && cardData[2] && cardData[2].content ? cardData[2].content : ''}
                                    <br />
                                    {'"a benevolent smile"'}
                                </Typography>
                            </CardContent>
                        </Card>
                    </div>
                </div>
            </div>
        )
    }
}
function mapStatetoProps(state) {
    return {
        communityMenu: state
    }
}

export default connect(mapStatetoProps)(withStyles(styles, { withTheme: true })(CardFolder))
