import * as React from 'react'
import * as a from '../files/assets/social@2x.png'
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import { withStyles, WithStyles } from '@material-ui/core/styles';
//import * as a from '../files/assets/social@2x.png'
import './loan-field.css'

const styles = theme => ({
    card: {
        width: 300,
        height:400

      },
      media: {
        height: 0,
        paddingTop: '56.25%'
        // 16:9
      },
    
  })

class Menu extends React.Component {
   
    render() {
        const refInput=React.createRef()
        const {
            classes
        } = this.props

        window.addEventListener('scroll', () => {
            const scrolled = window.pageYOffset;
            const val = scrolled * 0.5;
            console.log("ref",refInput.current,refInput.current.offset)
    refInput.current.style.animationIterationCount = `1`;
        })
        return (
            <div className="pa2 bg-black-05">
                <div className="flex w-100 justify-center parallax">
                    <h1>OUR MENU</h1>
                </div>
                <div className="flex flex-row justify-center items-center parallax-container " ref={refInput}>
                    <div className="flex w-60 flex-row justify-center pt4 mt3 ">
                        <div className="flex flex-column mh3 mt3" >
                            <Card className={classes.card}>
                                <CardMedia
                                  className={classes.media}
                                    image="http://tachyons.io/img/cat-720.jpg"
                                    title="Paella dish"
                                />
                                <CardContent>{this.props.cardContent}</CardContent>
                            </Card>
                        </div>
                        <div className="flex flex-column mh3 mb">
                        <Card className={classes.card}>
                                <CardMedia
                                  className={classes.media}
                                    image="http://tachyons.io/img/cat-720.jpg"
                                    title="Paella dish"
                                />
                                <CardContent>{this.props.cardContent}</CardContent>
                            </Card>
                        </div>
                        <div className="flex flex-column mh3 mt3">
                        <Card className={classes.card}>
                                <CardMedia
                                  className={classes.media}
                                    image="http://tachyons.io/img/cat-720.jpg"
                                    title="Paella dish"
                                />
                                <CardContent>{this.props.cardContent}</CardContent>
                            </Card>
                        </div>
                        
                        <div className="flex flex-column mh3 mb3">
                        <Card className={classes.card}>
                                <CardMedia
                                  className={classes.media}
                                    image="http://tachyons.io/img/cat-720.jpg"
                                    title="Paella dish"
                                />
                                <CardContent>{this.props.cardContent}</CardContent>
                            </Card>
                        </div>
                        </div>
                </div>
                <div className="parallax"></div>
                    
            </div>
        )
    }
}

export default withStyles(styles, { withTheme: true })(Menu)
