import  {RECIPE_VALUE,ERROR} from './actionTypes'
import * as recipe from '../data/recipe.json'

    export const recipeAction = () => dispatch => {
    
        fetch('/recipe')
        .then(res => dispatch({
            type: RECIPE_VALUE,
            payload: recipe
    }),
        
        
        )
        .catch(err => dispatch({ type: ERROR, payload: err }))
    }
